INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (10, 4, 'График дистанционных занятий', '<table><thead><tr><td><strong>1 урок</strong></td><td>9:00 — 9:30</td></tr></thead><tbody><tr><td><strong>2 урок</strong></td><td>9:40 — 10:10</td></tr><tr><td><strong>3 урок</strong></td><td>10:20 — 10:50</td></tr><tr><td><strong>4 урок</strong></td><td>11:00 — 11:30</td></tr><tr><td><strong>5 урок</strong></td><td>11:45 — 12:15</td></tr><tr><td><strong>6 урок</strong></td><td>12:25 — 12:55</td></tr><tr><td><strong>7 урок</strong></td><td>13:00 — 13:30</td></tr></tbody></table>', 500, 'active', '2023-10-14 14:10:19', '2023-10-14 14:10:19');
INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (11, 4, 'Реализуемые уровни образования', '<p>МКОУ Филипповская СОШ осуществляет образовательный процесс в соответствии с общеобразовательными программами трех уровней общего образования:</p><ul><li>I уровень – начальное общее образование (нормативный срок освоения 4 года);</li><li>II уровень – основное общее образование (нормативный срок освоения 5 лет);</li><li>III уровень – среднее общее образование (нормативный срок освоения 2 года).</li></ul><p><strong>Численность обучающихся по реализуемым образовательным программам:</strong></p><ul><li>Начальное общее образование -106 обучающихся;</li><li>Основное общее образование — 106 обучающихся;</li><li>Среднее общее образование — 20 обучающихся.</li></ul>', 500, 'active', '2023-10-14 14:10:19', '2023-10-14 14:10:19');
INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (12, 4, 'Языки, на которых осуществляется образование', '<p>Обучение и воспитание в МКОУ «Филипповская средняя общеобразовательная школа» ведётся на русском языке.</p>', 500, 'active', '2023-10-14 14:10:19', '2023-10-14 14:10:19');
INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (13, 4, 'Нормативные сроки обучения', '<ul><li>I уровень – начальное общее образование (нормативный срок освоения 4 года);</li><li>II уровень – основное общее образование (нормативный срок освоения 5 лет);</li><li>III уровень – среднее общее образование (нормативный срок освоения 2 года).</li></ul>', 500, 'active', '2023-10-14 14:10:19', '2023-10-14 14:10:19');
INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (14, 4, 'Срок действия государственной аккредитации образовательной программы', '<p>Смотреть в разделе&nbsp;<a href="http://xn----7sbbf3aapcdg1afga6a3b5e8f.xn--p1ai/sveden/document/">Документы</a></p>', 500, 'active', '2023-10-14 14:10:19', '2023-10-15 21:48:32');
INSERT INTO SAPI.sections (id, chapter_id, sectionTitle, sectionText, sequence, status, created_at, updated_at) VALUES (15, 4, 'Образовательные программы', '<p>Содержание общего образования определяется образовательными программами, разрабатываемыми и реализуемыми МКОУ Филипповской СОШ самостоятельно на основе федеральных государственных образовательных стандартов и примерных образовательных учебных программ, курсов, дисциплин.</p><p>Освоение образовательных программ основного общего и среднего общего образования завершается обязательной итоговой аттестацией обучающихся. Государственная итоговая аттестация выпускников МКОУ Филипповской СОШ осуществляется в соответствии с положением о Государственной итоговой аттестации выпускников общеобразовательных учреждений, утверждаемым Министерством образования и науки Российской Федерации.</p><p><strong>Виды реализуемых программ:</strong></p><p>а) Основные общеобразовательные программы начального общего образования;</p><p>б) Основные общеобразовательные программы основного общего образования;</p><p>в) Основные общеобразовательные программы среднего общего образования;</p><p>г) Адаптированная образовательная программа НОО и ООО.</p>', 500, 'active', '2023-10-14 14:10:19', '2023-10-14 14:10:19');
